package lec_2;
import java.util.*;

public class even_odd {

	public static void main(String[] args) {
		Scanner scn = new Scanner (System.in);
		int a = scn.nextInt();
		
		if ( a % 2== 0) {
			System.out.println(a + " is even");
			
		}
		
		else {
			System.out.println(a + " is odd");
		}
		// TODO Auto-generated method stub

	}

}
